function toHome(){window.location.href="/Home/Index"}function selectAll(){for(var i=document.getElementById("checkedAll"),t=document.getElementsByClassName("itemCheck"),n=0;n<t.length;n++)t[n].checked=i.checked}function PopulateSecondKinds(){var n=$("#FirstKind").val();$.ajax({url:"/Hire/PopulateSecondKinds",type:"GET",dataType:"JSON",data:{firstKindId:n},success:function(n){$("#SecondKind").html('<option value="">-- 选择岗位类别 --<\/option>');$.each(n,function(n,t){console.log(t);$("#SecondKind").append($("<option><\/option>").val(t.jobKindId).html(t.text))})}})}function PopulateCities(){var n=$("#ProvinceId").val();$.ajax({url:"/Hire/PopulateCities",type:"GET",dataType:"JSON",data:{provinceId:n},success:function(n){$("#CityId").html('<option value="">-- 选择市 --<\/option>');$("#LocationId").html('<option value="">-- 选择区 --<\/option>');$.each(n,function(n,t){console.log(t);$("#CityId").append($("<option><\/option>").val(t.locationId).html(t.text))})}})}function PopulateDistricts(){var n=$("#CityId").val();$.ajax({url:"/Hire/PopulateDistricts",type:"GET",dataType:"JSON",data:{cityId:n},success:function(n){$("#LocationId").html('<option value="">-- 选择区 --<\/option>');$.each(n,function(n,t){console.log(t);$("#LocationId").append($("<option><\/option>").val(t.locationId).html(t.text))})}})}function ChangeEmail(){var n=$("#changeEmailAddress").val();$.ajax({url:"/Recruiter/ChangeEmail",type:"POST",dataType:"JSON",data:{emailAddress:n},success:function(n){n.succeeded==!0?window.location.reload():(console.log(n),alert("修改失败"))}})}function ChangePhoneNumber(){var n=$("#changePhoneNumber").val();$.ajax({url:"/Recruiter/ChangePhoneNumber",type:"POST",dataType:"JSON",data:{phoneNumber:n},success:function(n){n.succeeded==!0?window.location.reload():(console.log(n),alert("修改失败"))}})}